import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation} from 'react-router-dom';

const Payment = () => {
//   const [paymentData, setPaymentData] = useState(null);
  const location = useLocation()

  useEffect(() => {
    // Make an API call to get payment data
    const fetchPaymentData = async () => {
      try {
        const response = await axios.get('http://localhost:8080/api/payment/getPaymentData');
        setPaymentData(response.data);
      } catch (error) {
        console.error('Error fetching payment data:', error);
      }
    };

    fetchPaymentData();
  }, []);

  const initPayment = () => {
    if (!paymentData) {
      console.error('Payment data not available');
      return;
    }

    const options = {
      key: "YOUR_RAZORPAY_KEY", // Replace with your actual Razorpay API key
      amount: paymentData.amount,
      currency: paymentData.currency,
      name: paymentData.name,
      description: paymentData.description,
      image: paymentData.image,
      order_id: paymentData.order_id,
      handler: async (response) => {
        try {
          const verifyUrl = "http://localhost:8080/api/payment/verify";
          const { data } = await axios.post(verifyUrl, response);
          console.log(data);
        } catch (error) {
          console.log(error);
        }
      },
      theme: {
        color: "#3399cc",
      },
    };

    const rzp = new window.Razorpay(options);
    rzp.open();
  };

  return (
    <div>
      <h1>Payment Page</h1>
      {paymentData && (
        <div>
          <p>Amount: {paymentData.amount}</p>
          {/* <p>Description: {paymentData.description}</p> */}
          <button onClick={initPayment}>Proceed to Pay</button>
        </div>
      )}
    </div>
  );
};

export default Payment;
