// import React from "react";
// import { useLocation} from 'react-router-dom';

// function Payment(){
// 	const location = useLocation();
// 	console.log(location);
// 	return(

// 		<h1>payment</h1>

// 	)
// }
// export default Payment
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation} from 'react-router-dom';


const Payment = () => {
  const [paymentData, setPaymentData] = useState(null);
  const location = useLocation()
  console.log(location.state);
  const fetchData = async () => {
    try {
      const response = await fetch(`http://localhost:4000/viewproductbyid`, {
        method: 'post',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ id: location.state.id }),
      });

      const result = await response.json();
      setPaymentData(result);
      console.log(result);
    } catch (error) {
      console.error('Error fetching product data:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, [location.state.id]);

  const initPayment = () => {
    if (!paymentData) {
      console.error('Payment data not available');
      return;
    }

    const options = {
      key: "rzp_test_LTI32T86DqAN8X", 
      amount: paymentData.amount,
      currency: paymentData.currency,
      name: paymentData.name,
      description: paymentData.description,
      image: paymentData.image,
      order_id: paymentData.order_id,
      handler: async (response) => {
        try {
          const verifyUrl = "http://localhost:8080/api/payment/verify";
          const { data } = await axios.post(verifyUrl, response);
          console.log(data);
        } catch (error) {
          console.log(error);
        }
      },
      theme: {
        color: "#3399cc",
      },
    };

    const rzp = new window.Razorpay(options);
    rzp.open();
  };

  return (
    <div>
      <h1>Payment Page</h1>
      {paymentData && (
        <div>
			<p>Product Name: {paymentData.ProductName}</p>
          <p>Amount: {paymentData.BidAmount}</p>
          
          <button onClick={initPayment}>Proceed to Pay</button>
        </div>
      )}
    </div>
  );
};

export default Payment;
