import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import './payment.css';

const Payment = () => {
  const [paymentData, setPaymentData] = useState(null);
  const location = useLocation();

  const fetchData = async () => {
	     try {
	       const response = await fetch(`http:localhost:4000/viewproductbyid`, {
	         method: 'post',
	         headers: {
	           'Content-Type': 'application/json',
	         },
	         body: JSON.stringify({ id: location.state.id }),
	       });
	
	       const result = await response.json();
	       setPaymentData(result);
	       console.log(result);
	     } catch (error) {
	       console.error('Error fetching product data:', error);
	     }
	   };
	
	useEffect(() => {
		fetchData();
	}, [location.state.id]);

  const initPayment = () => {
    if (!paymentData) {
      console.error('Payment data not available');
      return;
    }

    const options = {
      key: "rzp_test_LTI32T86DqAN8X",
      amount: paymentData.BidAmount * 100, // Convert to paisa if necessary
      currency: "INR",
      name: paymentData.ProductName,
      description: paymentData.Description,
      image: `http://localhost:4000/${paymentData.Images[0]}`,
      order_id: paymentData.order_id, // Assuming _id is the unique identifier for the order
      handler: async (response) => {
        try {
          const verifyUrl = "http://localhost:8080/api/payment/verify";
          const { data } = await axios.post(verifyUrl, response);
          console.log(data);
        } catch (error) {
          console.log(error);
        }
      },
      theme: {
        color: "#3399cc",
      },
    };

    const rzp = new window.Razorpay(options);
    rzp.open();
  };

  return (
    <div>
      {!paymentData && <p>Loading...</p>}
      {paymentData && (
        <div className="App">
          <div className="container">
            <img src={`http://localhost:4000/${paymentData.Images[0]}`} alt="product_img" className="img" />
            <p className="name">Product Name: {paymentData.ProductName}</p>
            <p className="price">
              Price: <span>&#x20B9; {paymentData.BidAmount}</span>
            </p>
            <button onClick={initPayment} className="buy_btn">
              Proceed to Pay
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Payment;
